    "likes": 5,
    "price": 50
  }
];

const SchoolSuplies = () => {
  const [cart, setCart] = useState([]);
  const addToCart = (toy) => setCart([...cart, toy]);
  // const removeFromCart = (id) => setCart(cart.filter(item => item.id !== id));

  return (
    <Container>
      <Row>
        {/* <button onClick={HandleClick}>Back</button> */}
        {toysData.map(toy => (
          <Col key={toy.id} xs={12} sm={6} md={4} lg={3}>
            <div className="toy-item">
              <h3>{toy.title}</h3>
              <Image src={toy.image} alt={toy.title} fluid className="toy-image" />
              <p>${toy.price}</p>
              <Button onClick={() => addToCart(toy)} >Add to Cart</Button>
            </div>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default SchoolSuplies;
